const staticData = $getWorkflowStaticData('global');

// ตรวจสอบว่าเป็นรอบแรกหรือไม่
if (!staticData.current_session_data) {
    // รอบแรก: เก็บข้อมูลทั้งหมดจาก input
    staticData.current_session_data = {
        original_question: $json["chatInput"],
        sessionId: $json["userId"] || "default-session",
        startTime: new Date().toISOString()
    };
}

// ส่งออกข้อมูลที่คงอยู่ทุกรอบ
return [{
    sessionId: staticData.current_session_data.sessionId,
    original_question: staticData.current_session_data.original_question
}];

