={{ (function() {
 const paramsString = $fromAI('Tool_Parameters');

 // Default query เพื่อดูรายชื่อตาราง
 const defaultParams = {
   query: "SELECT TABLE_NAME FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_TYPE = 'BASE TABLE' ORDER BY TABLE_NAME"
 };

 if (!paramsString) {
   return defaultParams;
 }

 try {
   // ลองแปลง JSON ก่อน
   const params = JSON.parse(paramsString);
   return {
     query: params.query || params.sql || params.statement || defaultParams.query
   };
 } catch (e) {
   // ถ้าแปลง JSON ไม่ได้ ถือว่าเป็น SQL string โดยตรง
   if (typeof paramsString === 'string' && paramsString.trim().length > 0) {
     return {
       query: paramsString.trim()
     };
   }
   return defaultParams;
 }
})() }}

={{ (function() {
  const paramsString = $fromAI('Tool_Parameters');

  // Default สำหรับ preview_table
  const defaultParams = {
    table_name: "allrawloanstat",
    limit: 10
  };

  if (!paramsString) {
    return defaultParams;
  }

  try {
    // ลองแปลง JSON ก่อน
    const params = JSON.parse(paramsString);
    return {
      table_name: params.table_name || params.tableName || params.table || defaultParams.table_name,
      limit: params.limit || params.row_count || params.rows || defaultParams.limit
    };
  } catch (e) {
    // ถ้าแปลง JSON ไม่ได้ ลอง parse SQL query
    if (typeof paramsString === 'string' && paramsString.trim().length > 0) {
      const sql = paramsString.trim().toUpperCase();

      // หา table name จาก FROM clause
      const fromMatch = sql.match(/FROM\s+(\w+)/);
      const tableName = fromMatch ? fromMatch[1].toLowerCase() : defaultParams.table_name;

      // หา limit จาก TOP clause
      const topMatch = sql.match(/TOP\s+(\d+)/);
      const limit = topMatch ? parseInt(topMatch[1]) : defaultParams.limit;

      return {
        table_name: tableName,
        limit: limit
      };
    }
    return defaultParams;
  }
})() }}

